import React,{Component} from 'react';
import Post from '../Post/Post';
import './Blog.css';
import axios from 'axios';



//basically it displays all the post 
class Blog extends Component{
    state={
        post:[],
        selectedId: null
    }
    
    componentDidMount(){
        axios.get('http://jsonplaceholder.typicode.com/posts')
             .then(response => {
                 console.log(response);
                 const posts=response.data;
                 const updatedPost=posts.map(post=>{

                    return {
                        ...post,
                        author:'Swati'
                    }});
                this.setState({post:updatedPost});
                
             })
    }

    fullPostHandler=(postId)=>{
       this.setState({selectedId:postId})

    }
    render(){
        const posts=this.state.post.map(posts=>{
            return <Post  key={post.id}
                          title={posts.title}
                          author={posts.author}
                          clicked={()=>this.fullPostHandler(post.id)}
                         />
        })
        return (
            <div className="Blog">
            <h1>BLOGS</h1>
                <section>
                    {posts}

                </section>
            </div>
        );

    }
}

export default Blog;
