import React, {Component} from 'react';  

/* Import Components */


class FormContainer extends Component {  
    constructor(props) {
        super(props);
           state = {
                  newUser: {
                    name: '',
                    email: '',
                    age: '',
              }
            }
            this.handleChange = this.handleChange.bind(this);
            this.formSubmitHandler = this.formSubmitHandler.bind(this);

    }

    formChangeHandler=(event)=>{
        const temp={...this.state.newUser};
        temp.name=event.target.value;

        this.setState({ newUser: temp });
        event.preventDefault();
    

    }
    
  formSubmitHandler=(event)=>{
    console.log(this.state.newUser);
    event.preventDefault();
   
  }
 
  render() {
    return (
      <form onSubmit={this.formSubmitHandler}>

        <input type="text" name="Name" placeholder="Enter your name here" onChange={this.nameChangeHandler}/> 
        <input type="email" name="Email" placeholder="Enter your email here" onChange={this.formChangeHandler}/> 
        <input type="text" name="Age" placeholder="Enter your age" onChange={this.formChangeHandler} /> 
        <button type="Submit">Submit</button>
        
      </form>
    );
  }
}

export default FormContainer;
