import React, {Component} from 'react'; 
import classes from './FormContainer.css'; 
import Input from '../UI/Input/Input';


/* Import Components */


class FormContainer extends Component {  
    constructor(props) {
        super(props);
        this.state = {
                  newUser: {
                    name: '',
                    email: '',
                    age: '',
              }
            }
            this.nameChangeHandler = this.nameChangeHandler.bind(this);
            this.emailChangeHandler = this.emailChangeHandler.bind(this);
            this.ageChangeHandler = this.ageChangeHandler.bind(this);
            this.formSubmitHandler = this.formSubmitHandler.bind(this);

    }

    nameChangeHandler=(event)=>{
       
        let tempUser={...this.state.newUser}
        tempUser.name= event.target.value;
        this.setState({ newUser: tempUser});
       
        event.preventDefault();
  
    }

    emailChangeHandler=(event)=>{
       
        let tempUser={...this.state.newUser}
        tempUser.email= event.target.value;
        this.setState({ newUser: tempUser});
       
        event.preventDefault();
  
    }

    ageChangeHandler=(event)=>{
       
        let tempUser={...this.state.newUser}
        tempUser.age= event.target.value;
        this.setState({ newUser: tempUser});
      
        event.preventDefault();
  
    }
    
  formSubmitHandler=(event)=>{
    console.log(this.state.newUser);
    event.preventDefault();
   
  }
 
  render() {
    return (
      <form className={classes.FormContainer} onSubmit={this.formSubmitHandler}>
        
        <Input type="text" name="Name" placeholder="Enter your name here" onChange={this.nameChangeHandler}/> 
        <Input type="email" name="Email" placeholder="Enter your email here" onChange={this.emailChangeHandler}/> 
        <Input type="text" name="Age" placeholder="Enter your age" onChange={this.ageChangeHandler} /> 
        <button type="Submit">Submit</button>
        
      </form>
    );
  }
}

export default FormContainer;
